INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'fr_FR', '{WEB_TITLE} - Quelqu'un a une question au sujet de votre annonce', '<p>Bonjour {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) vous a laissé un message au sujet de votre annonce <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>Cordialement,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'fr_FR', 'Merci de valider votre compte sur {WEB_TITLE}', '<p>Bonjour {USER_NAME},</p><p>Veuillez valider votre inscription en cliquant sur le lien suivant: {VALIDATION_LINK}</p><p>Merci!</p><p>Cordialement,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'fr_FR', '{WEB_TITLE} - Inscription effectuée avec succès!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'fr_FR', 'Regarde ce que j'ai trouvé sur {WEB_TITLE}', '<p>Bonjour {FRIEND_NAME},</p><p>Votre ami {USER_NAME} souhaite partager cette annonce avec vous:<a href="{ITEM_URL}">{ITEM_TITLE}</a></p><p>Message:</p><p>{COMMENT}</p><p>Cordialement,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'fr_FR', '{WEB_TITLE} - Nouvelle annonce in the last hour', '<p>Bonjour {USER_NAME},</p><p>De nouvelles annonces ont été publiées il y a moins d'une heure. Jetez y un coup d'oeil :</p><p>{ADS}</p><p>-------------</p><p>Pour vous désabonner de cette alerte, cliquez ici : {UNSUB_LINK}</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'fr_FR', '{WEB_TITLE} - Nouvelle annonce in the last day', '<p>Bonjour {USER_NAME},</p><p>De nouvelles annonces ont été publiées il y a moins de 24h. Jetez y un oeil:</p><p>{ADS}</p><p>-------------</p><p>Pour vous désabonner de cette alerte, cliquez ici: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'fr_FR', '{WEB_TITLE} - Nouvelle annonce in the last week', '<p>Bonjour {USER_NAME},</p><p>De nouvelles annonces ont été publiées il y a moins de 7 jours. Jetez y un oeil:</p><p>{ADS}</p><p>-------------</p><p>Pour vous désabonner de cette alerte, cliquez ici: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'fr_FR', '{WEB_TITLE} - Nouvelle annonce', '<p>Bonjour {USER_NAME},</p><p>Une nouvelle annonce vient d’être publiée, jetez-y un oeil:</p><p>{ADS}</p><p>-------------</p><p>Vous recevez cet email automatique car vous êtes abonné à une alerte qui vous tient au courant des nouvelles annonces publiées. Pour vous désabonner de cette alerte, cliquez ici: {UNSUB_LINK}</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'fr_FR', '{WEB_TITLE} - Il y a un nouveau commentaire à propos de votre annonce', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'fr_FR', '{WEB_TITLE} - Gestion de votre annonce {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'fr_FR', '{WEB_TITLE} - Validez votre annonce', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'fr_FR', '{WEB_TITLE} - Votre alerte: une nouvelle annonce a été publiée', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'fr_FR', '{WEB_TITLE} - Récupération de votre mot de passe', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'fr_FR', '{WEB_TITLE} - Vous avez demandé un changement d'adresse e-mail', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'fr_FR', '{WEB_TITLE} - Merci de valider votre alerte', '<p>Bonjour {USER_NAME},</p><p>Veuillez svp confirmer votre inscription à cette alerte en cliquant sur le lien suivant:{VALIDATION_LINK}</p><p>Merci</p><p>Cordialement,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'fr_FR', '{WEB_TITLE} - Votre commentaire a été accepté', '<p>Bonjour {COMMENT_AUTHOR},</p><p>Votre commentaire à propos de <a href="{ITEM_URL}">{ITEM_TITLE}</a> a été approuvé.</p><p>Salutations,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'fr_FR', '{WEB_TITLE} - Validez votre annonce', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'fr_FR', '{WEB_TITLE} - Un nouvel utilisateur vient de s'inscrire', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'fr_FR', '{WEB_TITLE} - Vous avez été contacté. Une question vous attend.', '<p>Bonjour {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) vous a laissé un message:</p><p>{COMMENT}</p><p>Cordialement,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'fr_FR', '{WEB_TITLE} - Un nouveau commentaire vient d'être fait au sujet de votre annonce', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'fr_FR', '{WEB_TITLE}  Création du compte Administrateur réussie', '<p>Salut {ADMIN_NAME},</p><p>L'Administrateur de {WEB_LINK} vous a créé votre compte</p><ul><li>Nom d'utilisateur: {USERNAME}</li><li>Mot de passe: {PASSWORD}</li></ul><p>Vous pouvez accéder au tableau de bord ici: {WEB_ADMIN_LINK}.</p><p>Merci</p><p>Meilleures Salutations,</p> ');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'fr_FR', '{WEB_TITLE}  Votre annonce est sur le point d'expirer!', '<p>Bonjour {USER_NAME},</p><p>Votre annonce <a href="{ITEM_URL}">{ITEM_TITLE}</a> sur {WEB_LINK} va bientôt expirer. ');
