INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'uk_UK', '{WEB_TITLE} - У когось є питання про ваше оголошення', '<p>Привіт {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) залишив Вам повідомлення про Ваше оголошення <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'uk_UK', 'Прохання підтвердити ваш {WEB_TITLE} обліковий запис', '<p>Привіт {USER_NAME},</p><p> підтвердіть реєстрацію, натиснувши на посилання: {VALIDATION_LINK}</p><p>Спасибі!</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'uk_UK', '{WEB_TITLE} - Успішна реєстрація!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'uk_UK', 'Поглянь, що я виявив на {WEB_TITLE}', '<p>Привіт {USER_NAME},</p><p>Ви отримали цей лист, тому що ви опублікували оголошення на {WEB_LINK}. Будь ласка, перевірте оголошення натиснувши на посилання: {VALIDATION_LINK}. Якщо ви не публікували оголошення, не звертайте уваги на цей лист.</p><p>Деталі:</p><p>Контактне ім'я: {USER_NAME}<br />Контактний e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Навіть якщо ви не зареєстровані на сайті {WEB_LINK}, ви все ще можете змінити або видалити ваше оголошення:</p><p>Ви можете відредагувати оголошення , перейшовши за цим посиланням: {EDIT_LINK}</p><p>Ви можете видалити оголошення, перейшовши за цим посиланням: {DELETE_LINK}</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'uk_UK', '{WEB_TITLE} - Нове оголошення in the last hour', '<p>Привіт {USER_NAME},</p><p>Нові оголошення опубліковані за останню годину. Поглянь на них:</p><p>{ADS}</p><p>-------------</p><p>Щоб відписатися від цього попередження, перейдіть за посиланням: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'uk_UK', '{WEB_TITLE} - Нове оголошення in the last day', '<p>Привіт {USER_NAME},</p><p>Нові оголошення за минулий день. Поглянь на них:</p><p>{ADS}</p><p>-------------</p><p>Щоб відписатися від цього попередження, перейдіть за посиланням: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'uk_UK', '{WEB_TITLE} - Нове оголошення in the last week', '<p>Привіт {USER_NAME},</p><p>Нові оголошення опубліковані минулого тижня. Поглянь на них:</p><p>{ADS}</p><p>-------------</p><p>Щоб відписатися від цього попередження, перейдіть за посиланням: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'uk_UK', '{WEB_TITLE} - Нове оголошення', '<p>Привіт {USER_NAME},</p><p>Опубліковано нове оголошення!</p><p>{ADS}</p><p>-------------</p><p>Для того, щоб відписатися від цього попередження, перейдіть за посиланням: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'uk_UK', '{WEB_TITLE} - Новий коментар', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'uk_UK', '{WEB_TITLE} - Можливості редагування для оголошення {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'uk_UK', '{WEB_TITLE} - Підтвердження оголошення', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'uk_UK', '{WEB_TITLE} - Опубліковано нове оголошення', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'uk_UK', '{WEB_TITLE} - Відновлення пароля', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'uk_UK', '{WEB_TITLE} - Запит на зміну пошти', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'uk_UK', '{WEB_TITLE} - Будь Ласка, підтвердьте оповіщення', '<p>Привіт {USER_NAME},</p><p> підтвердіть реєстрацію, натиснувши на посилання: {VALIDATION_LINK}</p><p>Спасибі!</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'uk_UK', '{WEB_TITLE} - Ваш коментар схвалений', '<p>Привіт {COMMENT_AUTHOR},</p><p>Ваш коментар <a href="{ITEM_URL}">{ITEM_TITLE}</a> успішно затверджено.</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'uk_UK', '{WEB_TITLE} - Підтвердження оголошення', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'uk_UK', '{WEB_TITLE} - Реєстрація нового користувача', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'uk_UK', '{WEB_TITLE} - У когось є питання до Вас', '<p>Привіт {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) залишив повідомлення:</p><p>{COMMENT}</p><p>З повагою,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'uk_UK', '{WEB_TITLE} - Хтось залишив коментар на оголошення', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'uk_UK', '{WEB_TITLE} - Успіх створення облікового запису адміністратора!', '<p>Привіт {ИМЯ_АДМИНИСТРАТОРА},</p><p>admin {WEB_LINK} створив для вас обліковий запис,</p><ul><li>ім'я Користувача: {USERNAME}</li><li>Пароль: {PASSWORD}</li></ul><p>Ви можете отримати доступ до адмін-панелі, тут {WEB_ADMIN_LINK}.</p><p>Спасибі!</p><p>з Повагою,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'uk_UK', '{WEB_TITLE} - Термін вашого оголошення закінчився', '<p>Привіт {USER},</p><p>Термін вашого оголошення, <a href="{ITEM_URL}">{ITEM_TITLE}</a> закінчується {WEB_LINK}.');
